import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../css/code.css";
import { Fragment } from "react";
import SmileyFace from "./SmileyFace";

function Code({ id }) {
  const codeBlocks = [
    // Your codeBlocks data...
  ];
  const [data, setData] = useState(codeBlocks);
  const [studentOrMentor, setStudentOrMentor] = useState("student");
  const [newCode, setNewCode] = useState("");
  const [isCorrect, setIsCorrect] = useState(false); // New state to track correctness

  function handleSubmit() {
    const correctedCode = data.find((code) => code.id === id).correctedCode;
    setIsCorrect(newCode === correctedCode); // Compare the new code with correctedCode
  }

  return (
    <div>
      {data.map((code) =>
        code.id === id ? (
          <Fragment key={code.id}>
            <h1>the subject of the lesson: {code.title}</h1>
            {studentOrMentor === "mentor" ? (
              <p>{code.correctedCode}</p>
            ) : (
              <div>
                <textarea
                  onChange={(e) => setNewCode(e.target.value)}
                  value={newCode}
                >
                  {code.testCode}
                </textarea>
                <button onClick={handleSubmit}>Save</button>
                {/* Conditionally render the SmileyFace */}
                {isCorrect && <SmileyFace />}
              </div>
            )}
          </Fragment>
        ) : null
      )}
    </div>
  );
}

export default Code;
