import React, { useEffect, useState } from "react";
import Code from "../components/Code";
import io from "socket.io-client";
const CodeBlockPage2 = () => {
  const [code, setCode] = useState("");
  //false for student, true for mentor
  const [isStudent, setIsStudent] = useState(false);
  const id = "code-block-2";

  useEffect(() => {
    //http request to get code from backend - once
    fetch(`http://localhost:8000/code/${id}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data.code);
        setCode(data.code);
      })
      .catch((err) => console.log(err));

    const socket = io("http://localhost:8000");
    // setSocket(socket);
    socket.on("connect", () => {});
    socket.on("disconnect", () => {});
    socket.emit("join", { id: id });
    socket.on("message", (message) => {});

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      {code !== "" && <Code code={code} isStudent={isStudent} id={id} />}
    </div>
  );
};

export default CodeBlockPage2;
