import React, { useState, useEffect } from "react";
import "../css/code.css";
import hljs from "highlight.js";
import "highlight.js/styles/atom-one-dark.css";
import { Fragment } from "react";
import SmileyFace from "./SmileyFace";
import useSock from "./UseSock";

function Code({ id, isStudent, code, onCodeChange }) {
  const { socket } = useSock();

  // New code of the student solution
  const [newCode, setNewCode] = useState("");
  const [studentsCode, setStudentsCode] = useState(code.textCode);
  const [isCodeCorrect, setIsCodeCorrect] = useState(false);
  const [isMentorLeft, setIsMentorLeft] = useState(false);

  socket?.on("newCodeOfStudent", (code) => {
    console.log("new code from student: ", code);
    setStudentsCode(code);
  });

  socket?.on("mentorLeave", (data) => {
    console.log(data);
    setIsMentorLeft(true);
  });

  socket?.on("mentorJoin", (data) => {
    console.log(data);
    setIsMentorLeft(false);
  });

  // Check if the student code is correct
  function handleSubmit() {
    code.correctedCode === newCode
      ? setIsCodeCorrect(true)
      : setIsCodeCorrect(false);
  }

  // Function to highlight the code with hljs and return as HTML
  const highlightCode = (code) => {
    const highlightedCode = hljs.highlightAuto(code).value;
    return { __html: highlightedCode };
  };

  useEffect(() => {
    // Highlight the default value and update syntax highlighting when the code changes
    const highlightedDefaultValue = hljs.highlightAuto(code.testCode).value;
    setStudentsCode(highlightedDefaultValue);
    hljs.highlightBlock(document.getElementById("codeTextarea"));
  }, [code.testCode]);

  return (
    <div>
      <h1>the subject of the lesson: {code.title}</h1>
      <h2>hey i am the: {isStudent ? "Student" : "Mentor"}</h2>
      {isCodeCorrect && isStudent && <SmileyFace />}
      <pre>
        <code
          id="codeTextarea"
          className={`language-javascript`}
          dangerouslySetInnerHTML={highlightCode(studentsCode)}
        />
      </pre>
      <textarea
        type="text"
        defaultValue={code.testCode}
        value={newCode}
        onChange={(e) => {
          e.preventDefault();
          onCodeChange(e.target.value);
          setNewCode(e.target.value);
        }}
        // False for student, true for mentor
        disabled={!isStudent}
      />
      {isStudent ? <button onClick={handleSubmit}>Save</button> : null}
      {isMentorLeft && <h1>the mentor left the room</h1>}
    </div>
  );
}

export default Code;
