      console.log("Student's code is incorrect.");
    }

    return (
      <div>
        {data.map((code) =>
          code.id === id ? (
            <Fragment key={code.id}>
              <h1>the subject of the lesson :{code.title}</h1>
              {userOrMentor === "mentor" ? (
                <p>{code.testCode}</p>
