import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import "../css/code.css";
import hljs from "highlight.js";
import "highlight.js/styles/atom-one-dark.css";
import CodeHighlight from "./CodeHighlight";
import { Fragment } from "react";
import SmileyFace from "./SmileyFace";

function Code({ id, isStudent, code, onCodeChanged }) {
  console.log(id);
  console.log(code);

  //new code of the stident sulotion
  const [newCode, setNewCode] = useState("");

  const codeRef = useRef(null);

  // When the code.testCode or newCode changes, update syntax highlighting
  useEffect(() => {
    hljs.highlightBlock(codeRef.current);
  }, [code.testCode, newCode]);

  //check if the student code is correct
  function handleSubmit() {
    {
      code.correctedCode === newCode ? alert("correct") : alert("incorrect");
    }
  }

  return (
    <div>
      <h1>the subject of the lesson :{code.title}</h1>
      <h2>hey i am the:{isStudent ? "Mentor" : "Student"}</h2>
      {/* <CodeHighlight code={code.testCode} language="javascript" ref={codeRef} /> */}
      <input
        type="text"
        defaultValue={code.testCode}
        onChange={(e) => {
          onCodeChanged(e.target.value);
          setNewCode(e.target.value);
        }}
        //false for student, true for mentor
        editable={!isStudent}
      />
      {isStudent ? null : <button onClick={handleSubmit}>Save</button>}
    </div>
  );
}

export default Code;
