import React, { useState, useEffect } from "react";
import hljs from "highlight.js";
import "highlight.js/styles/atom-one-dark.css";

function SyntaxHighlightedTextarea({ value, onChange, isEditable }) {
  const [highlightedValue, setHighlightedValue] = useState("");

  useEffect(() => {
    setHighlightedValue(hljs.highlightAuto(value).value);
  }, [value]);

  // Function to escape HTML characters
  const escapeHTML = (html) => {
    return html
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;");
  };

  return (
    <pre>
      <code
        contentEditable={isEditable}
        dangerouslySetInnerHTML={{ __html: escapeHTML(highlightedValue) }}
        onBlur={(e) => {
          onChange(e.target.textContent);
        }}
      />
    </pre>
  );
}

export default SyntaxHighlightedTextarea;
