import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../css/code.css";
// import axios from "axios";
import { Fragment } from "react";
import SmileyFace from "./SmileyFace";

function Code({ id, isStudent, code, onCodeChanged }) {
  console.log(id);
  console.log(code);

  const [data, setData] = useState({ code });
  //new code of the stident sulotion
  const [newCode, setNewCode] = useState("");

  //check if the student code is correct
  function handleSubmit() {
    data.find((code) => code.id === id).correctedCode === newCode
      ? alert("correct")
      : alert("incorrect");
  }

  return (
    <div>
      <h1>the subject of the lesson :{code.title}</h1>
      <h2>hey i am the:{isStudent ? "Mentor" : "Student"}</h2>
      <input
        type="text"
        defaultValue={code.testCode}
        onChange={(e) => {
          onCodeChanged(e.target.value);
          setNewCode(e.target.value);
        }}
        //false for student, true for mentor
        editable={!isStudent}
      />
      {isStudent ? null : <button onClick={handleSubmit}>Save</button>}
    </div>
  );
}

export default Code;
