import React, { useState, useEffect } from "react";
import hljs from "highlight.js";
import "highlight.js/styles/atom-one-dark.css";

function SyntaxHighlightedTextarea({ value, onChange, isEditable }) {
  const [highlightedValue, setHighlightedValue] = useState(value);

  useEffect(() => {
    setHighlightedValue(hljs.highlightAuto(value).value);
  }, [value]);

  return (
    <pre>
      <code
        contentEditable={isEditable} // Control content editability based on isEditable prop
        dangerouslySetInnerHTML={{ __html: highlightedValue }}
        onBlur={(e) => {
          onChange(e.target.textContent);
        }}
      />
    </pre>
  );
}

export default SyntaxHighlightedTextarea;
