import React, { useEffect, useState } from "react";
import Code from "../components/Code";
import axios from "axios";
import io from "socket.io-client";
const CodeBlockPage2 = () => {
  const [code, setCode] = useState("");
  const [socket, setSocket] = useState(null);
  //false for student, true for mentor
  const [isStudent, setIsStudent] = useState(false);
  const id = "code-block-2";

  useEffect(() => {
    // Get data code by id from the server using Axios
    axios
      .get(`http://localhost:8000/code/${id}`)
      .then((response) => {
        setCode(response.data.code);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });

    const socket = io("http://localhost:8000");
    setSocket(socket);
    socket.on("connect", () => {});
    socket.emit("join", { id: id });
    socket.on("disconnect", () => {});

    socket.on("role", ({ isMentor }) => {
      // Set the 'isStudent' state based on the received role information
      setIsStudent(!isMentor);
    });

    socket.on("message", (message) => {});

    return () => {
      socket.disconnect();
    };
  }, []);

  ///send the new code
  const onCodeChange = (newCode) => {
    console.log("New code from student: ", newCode);
    if (socket) {
      socket.emit("newCode", { id: id, code: newCode });
    }
  };

  return (
    <div>
      {code !== "" && (
        <Code
          code={code}
          onCodeChange={onCodeChange}
          isStudent={isStudent}
          id={id}
        />
      )}
    </div>
  );
};

export default CodeBlockPage2;
