import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../css/code.css";
// import axios from "axios";
import { Fragment } from "react";
import SmileyFace from "./SmileyFace";
function Code({ id }) {
  const codeBlocks = [
    {
      id: "1",
      title: "closing parenthesis",
      testCode: "console.log('Hello, World!';",
      correctedCode: "console.log('Hello, World!');",
    },
    {
      id: "2",
      title: "components-react",
      testCode: "function app ()=>{};",
      correctedCode:
        "import React from 'react';function App() {return ();} export default App;",
    },
    {
      id: "3",
      title: "loop",
      testCode: "for (let i = 0; i < 5; i++) {",
      correctedCode: "for (let i = 0; i < 5; i++) {}",
    },
    {
      id: "4",
      title: "object+array",
      testCode: "child=[{name: 'child1' age: 1 },}]",
      correctedCode: "child=[{name: 'child1', age: 1, },]",
    },
  ];
  const [data, setData] = useState(codeBlocks);
  const [studentrOrMentor, setStudentOrMentor] = useState("student");
  const [newCode, setNewCode] = useState("");
  const [isCorrect, setIsCorrect] = useState(false);

  function handleSubmit() {
    const correctedCode = data.find((code) => code.id === id).correctedCode;
    setIsCorrect(newCode === correctedCode); // Compare the new code with correctedCode
  }

  return (
    <div>
      {data.map((code) =>
        code.id === id ? (
          <Fragment key={code.id}>
            <h1>the subject of the lesson :{code.title}</h1>
            {studentrOrMentor === "mentor" ? (
              <p>{code.correctedCode}</p>
            ) : (
              <div>
                <textarea onChange={(e) => setNewCode(e.target.value)}>
                  {code.testCode}
                </textarea>
                <button onClick={handleSubmit}>Save</button>
              </div>
            )}
          </Fragment>
        ) : null
      )}
    </div>
  );
}

export default Code;
