import React from "react";
import smileyHappy from "../images/smiley-happy.png";
import smileySad from "../images/sadSmiley.jpeg";

const SmileyFace = () => {
  //style the img tag

  const SmileyFace = () => {
    const [showSmiley, setShowSmiley] = useState(true);

    useEffect(() => {
      const timeout = setTimeout(() => {
        setShowSmiley(false);
      }, 5000);

      return () => clearTimeout(timeout);
    }, []);

    return (
      <div>
        {showSmiley && (
          <div>
            <img
              src={smileyHappy}
              alt="Smiley Face"
              style={{
                width: "50%",
                height: "10%",
                marginLeft: "25%",
                marginRight: "25%",
              }}
            />
            <p
              style={{
                textAlign: "center",
                fontSize: "40px",
                color: "blue",
              }}
            >
              good job!
            </p>
          </div>
        )}
      </div>
    );
  };
};
export default SmileyFace;
